@push('scripts')

<script src="/js/datatables.min.js"></script>

<script>
    $(document).ready(function () {

        var ajaxDatatable = $('#ajax-datatable');

        // Setup - add a text input to each footer cell
        ajaxDatatable.find('tfoot th').each( function () {
            var title = $(this).text();
            $(this).html( '<input type="text" placeholder="Search '+title+'" />' );
        } );

        var table = ajaxDatatable.DataTable({
            processing: true,
            serverSide: true,
            ajax: '{!! route('datatables.returnMerchandiseAuths') !!}',
            columns: [
                { data: 'number', name: 'number' },
                { data: 'request_date', name: 'request_date' },
                { data: 'invoice.sales_order.order_name', name: 'invoice.salesOrder.order_name' },
                { data: 'action', name: 'action', searchable: false, orderable: false },
            ]
        });

        // Apply the search
        table.columns().every( function () {
            var that = this;

            $( 'input', this.footer() ).on( 'keyup change', function () {
                if ( that.search() !== this.value ) {
                    that
                        .search( this.value )
                        .draw();
                }
            } );
        } );
    });

</script>
@endpush

